import React, { useState } from "react";
import {
  MDBInput,
  MDBBtn,
} from 'mdb-react-ui-kit';
import contactImage from '../../images/Version control-cuate.png'

const Contact=() => {
  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };
  return (
    <>
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-12">
            <h2 className="row justify-content-center">Contact Me</h2>
            <hr></hr>
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row gx-1 justify-content-around" >
          <div className="col-md-6">
            <div className="row justify-content-center" style={{ marginTop: '150px', textAlign: 'center' }}>
              <form onSubmit={handleSubmit}>
                <MDBInput id='form4Example1' wrapperClass='mb-4' label='Name' htmlFor="name"/>
                <MDBInput type='email' id='form4Example2' wrapperClass='mb-4' label='Email address' htmlFor="email" />
                <MDBInput wrapperClass='mb-4' textarea id='form4Example3' rows={4} label='Message' htmlFor="message" />
                <MDBBtn type='submit' className='mb-4' block>
                Submit
                </MDBBtn>
              </form>
            </div>
          </div>
          <div class="col-md-6">
            <img
              src={contactImage}
              alt="home pic"
              className="img-fluid"

            />
          </div>
        </div>
      </div>
    </>
  );
}

export default Contact;
