import React, { useState } from "react";
import {
  MDBInput,
  MDBBtn,
} from 'mdb-react-ui-kit';
import contactImage from '../../images/Version control-cuate.png'
import { validateEmail } from '../../utils/helpers';


const Contact=() => {
  const [errorMessage, setErrorMessage] = useState('');
  const [formState, setFormState] = useState({ name: '', email: '', message: '' });

  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!errorMessage) {
      console.log('Submit Form', formState);
    }
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:3000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };

  const handleChange = (e) => {
    if (e.target.name === 'email') {
      const isValid = validateEmail(e.target.value);
      if (!isValid) {
        setErrorMessage('Your email is invalid.');
      } else {
        setErrorMessage('');
      }
    } else {
      if (!e.target.value.length) {
        setErrorMessage(`${e.target.name} is required.`);
      } else {
        setErrorMessage('');
      }
    }
    if (!errorMessage) {
      setFormState({ ...formState, [e.target.name]: e.target.value });
      console.log('Handle Form', formState);
    }
  };

  return (
    <>
      <div className="container py-5">
        <div className="row justify-content-center">
          <div className="col-12">
            <h2 className="row justify-content-center">Contact Me</h2>
            <hr></hr>
          </div>
        </div>
      </div>
      <div className="container">
        <div className="row gx-1 justify-content-around" >
          <div className="col-md-6">
            <div className="row justify-content-center" style={{ marginTop: '150px', textAlign: 'center' }}>
              <form onSubmit={handleSubmit}>
                <MDBInput id='form4Example1' wrapperClass='mb-4' label='Name' htmlFor="name" defaultValue={name} onBlur={handleChange} required/>
                <MDBInput type='email' id='form4Example2' wrapperClass='mb-4' label='Email address' htmlFor="email" defaultValue={email} onBlur={handleChange} required />
                <MDBInput wrapperClass='mb-4' textarea id='form4Example3' rows={4} label='Message' htmlFor="message" defaultValue={message} onBlur={handleChange} required/>
                {errorMessage && (
          <div>
            <p className="error-text">{errorMessage}</p>
          </div>
        )}
                <MDBBtn type='submit' className='mb-4' block>
                {status}
                </MDBBtn>
              </form>
            </div>
          </div>
          <div class="col-md-6">
            <img
              src={contactImage}
              alt="home pic"
              className="img-fluid"

            />
          </div>
        </div>
      </div>
    </>
  );
}

export default Contact;
