import React, { useState } from "react";

import contactImage from '../../images/Get in touch-cuate.png'

const Contact = () => {
  const [status, setStatus] = useState("Submit");
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:3000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };

  return (
    <>
<div class="container">
  <div class="row gx-1 ">

    <div class="col-md-6">

<form onSubmit={handleSubmit}>
 
  <div class="form-outline mb-4">
    <input type="text" id="form4Example1" class="form-control" />
    <label class="form-label" htmlFor="name" for="form4Example1">Name</label>
  </div>

  
  <div class="form-outline mb-4">
    <input type="email" id="form4Example2" class="form-control" />
    <label class="form-label" htmlFor="email" for="form4Example2">Email address</label>
  </div>

 
  <div class="form-outline mb-4">
    <textarea class="form-control" id="form4Example3" rows="4"></textarea>
    <label class="form-label" htmlFor="message" for="form4Example3">Message</label>
  </div>

  <button type="submit" class="btn btn-primary btn-block mb-4">Send</button>
</form>
</div>

    <div class="col-md-6">
    <img
              src={contacImage}
              alt="home pic"
              className="img-fluid"

            />
    </div>

  </div>
</div>













     {/* <form onSubmit={handleSubmit}>
      <div>
